    private void dropTarget7Drop(java.awt.dnd.DropTargetDropEvent evt) {//GEN-FIRST:event_dropTarget7Drop
        files = Index.getDropFiles(evt);
        for (File file : files){
            System.out.println("File "+file.getName()+" droped");
            control.funFile = file;
        }
        end = true;
        dispose();
    }//GEN-LAST:event_dropTarget7Drop


